import React, { useEffect, useRef } from 'react';
import { LexicalComposer } from '@lexical/react/LexicalComposer';
import { RichTextPlugin } from '@lexical/react/LexicalRichTextPlugin';
import { ContentEditable } from '@lexical/react/LexicalContentEditable';
import { HistoryPlugin } from '@lexical/react/LexicalHistoryPlugin';
import { AutoFocusPlugin } from '@lexical/react/LexicalAutoFocusPlugin';
import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot, $getSelection, $isRangeSelection, $createTextNode, $isElementNode } from 'lexical';
import { Bold, Italic, Underline, List, Link, AlignLeft, AlignCenter, AlignRight, Heading1, Heading2, Heading3 } from 'lucide-react';

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  className?: string;
}

const IMAGE_UPLOAD_ENDPOINT = '/.netlify/functions/upload-image';

// Custom onChange plugin
function CustomOnChangePlugin({ onChange }: { onChange: (value: string) => void }): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(({ editorState }) => {
      editorState.read(() => {
        const root = $getRoot();
        const text = root.getTextContent();
        onChange(text);
      });
    });
  }, [editor, onChange]);

  return null;
}

// Markdown shortcuts plugin
function MarkdownShortcutsPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(({ editorState, dirtyElements }) => {
      if (dirtyElements.size === 0) return;

      editorState.read(() => {
        const selection = $getSelection();
        if (!$isRangeSelection(selection)) return;

        const text = selection.getTextContent();
        
        // Handle markdown shortcuts
        if (text.startsWith('**') && text.endsWith('**') && text.length > 4) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(2, -2);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('*') && text.endsWith('*') && text.length > 2) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(1, -1);
              newSelection.insertText(cleanText);
              newSelection.formatText('italic');
            }
          });
        } else if (text.startsWith('~') && text.endsWith('~') && text.length > 2) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(1, -1);
              newSelection.insertText(cleanText);
              newSelection.formatText('underline');
            }
          });
        } else if (text.startsWith('# ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(2);
              newSelection.insertText(cleanText);
              // Apply header formatting (simplified - just make it bold and larger)
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('## ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(3);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('### ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(4);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        }
      });
    });
  }, [editor]);

  return null;
}

// Toolbar component
function ToolbarPlugin(): JSX.Element {
  const [editor] = useLexicalComposerContext();

  const formatText = (format: 'bold' | 'italic' | 'underline') => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        selection.formatText(format);
      }
    });
  };

  const formatHeader = (level: 1 | 2 | 3) => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // For now, just make headers bold
        // In a full implementation, you'd create proper header nodes
        selection.formatText('bold');
      }
    });
  };

  const alignText = (alignment: 'left' | 'center' | 'right') => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // For now, just insert alignment markers
        // In a full implementation, you'd set proper CSS alignment
        const marker = alignment === 'left' ? '' : alignment === 'center' ? ' [CENTER] ' : ' [RIGHT] ';
        selection.insertText(marker);
      }
    });
  };

  const insertLink = () => {
    const url = prompt('Enter URL:');
    if (url) {
      const linkText = prompt('Enter link text (optional):', url);
      const displayText = linkText || url;
      
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          // Create markdown-style link
          const markdownLink = `[${displayText}](${url})`;
          selection.insertText(markdownLink);
        }
      });
    }
  };

  const toggleList = () => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // Simple list implementation - just add bullet points
        const text = selection.getTextContent();
        const lines = text.split('\n');
        const formattedLines = lines.map(line => `â€¢ ${line}`);
        selection.insertText(formattedLines.join('\n'));
      }
    });
  };

  return (
