// Smart URL detection plugin
function SmartURLDetectionPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(({ editorState, dirtyElements }) => {
      if (dirtyElements.size === 0) return;

      editorState.read(() => {
        const selection = $getSelection();
        if (!$isRangeSelection(selection)) return;

        const text = selection.getTextContent();
        
        // URL regex pattern - matches http/https URLs and www. URLs
        const urlRegex = /((https?:\/\/[^\s]+)|(www\.[^\s]+\.[^\s]+))/g;
        const matches = text.match(urlRegex);
        
        if (matches && matches.length > 0) {
          // Check if we have unformatted URLs
          const hasUnformattedUrls = matches.some(url => {
            // Check if the URL is not already in markdown link format
            return !text.includes(`[${url}]`) && !text.includes(`[${url.split('/').pop()}]`);
          });

          if (hasUnformattedUrls) {
            editor.update(() => {
              const newSelection = $getSelection();
              if ($isRangeSelection(newSelection)) {
                // Get the current text again
                const currentText = newSelection.getTextContent();
                
                // Replace URLs with markdown-style links
                let formattedText = currentText;
                matches.forEach(url => {
                  // Only format if it's not already formatted
                  if (!formattedText.includes(`[${url}]`)) {
                    // Prepend https:// if it's a www. URL
                    const fullUrl = url.startsWith('www.') ? `https://${url}` : url;
                    // Create a shorter display text for the link
                    const displayText = url.length > 50 ? url.substring(0, 47) + '...' : url;
                    formattedText = formattedText.replace(url, `[${displayText}](${fullUrl})`);
                  }
                });
                
                if (formattedText !== currentText) {
                  // Replace the entire selection with formatted text
                  newSelection.insertText(formattedText);
                }
              }
            });
          }
        }
      });
    });
  }, [editor]);

  return null;
}

// Error boundary component
function ErrorBoundary({ children }: { children: React.ReactNode }): JSX.Element {
  return <div>{children}</div>;
}

const RichTextEditor: React.FC<RichTextEditorProps> = ({ 
  value, 
  onChange, 
  placeholder = "Start writing...",
  className = ""
}) => {
  const editorRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Handle image file selection
  const handleImageChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      await uploadAndInsertImage(file);
    }
    // Reset file input
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  // Handle drag-and-drop
  const handleDrop = async (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    if (event.dataTransfer.files && event.dataTransfer.files.length > 0) {
      const file = event.dataTransfer.files[0];
      await uploadAndInsertImage(file);
    }
  };

  // Prevent default drag-over behavior
  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
  };

  // Upload image to Netlify function and insert <img> tag
  const uploadAndInsertImage = async (file: File) => {
    const formData = new FormData();
    formData.append('file', file);
    try {
      const response = await fetch(IMAGE_UPLOAD_ENDPOINT, {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (data.url) {
        // Insert <img> tag at cursor position
        insertImageAtCursor(data.url);
      } else {
        alert('Image upload failed: ' + (data.error || 'Unknown error'));
      }
    } catch (err) {
      alert('Image upload failed: ' + err.message);
    }
  };

  // Insert <img> tag at the current cursor position
  const insertImageAtCursor = (url: string) => {
    editorRef.current?.focus();
    // Use Lexical API to insert the image tag as HTML
    // (for simplicity, just insert the HTML string)
    // In a full Lexical implementation, you'd use a custom node
    const imgHtml = `<img src="${url}" alt="Image" style="max-width:100%;margin:12px 0;" />`;
    // Insert the image HTML at the cursor
    // We'll use document.execCommand for now for simplicity
    document.execCommand('insertHTML', false, imgHtml);
  };

  const initialConfig = {
    namespace: 'BeedooRichTextEditor',
    theme: {
      root: 'outline-none min-h-[200px] p-4',
      text: {
        bold: 'font-bold',
        italic: 'italic',
        underline: 'underline',
      },
    },
    onError: (error: Error) => {
      console.error('Lexical error:', error);
    },
  };

  return (
    <div className={`border border-gray-300 rounded-md ${className}`}
      ref={editorRef}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      <LexicalComposer initialConfig={initialConfig}>
        <ToolbarPlugin />
        <div className="relative">
          <RichTextPlugin
            contentEditable={
              <ContentEditable 
                className="outline-none min-h-[200px] p-4"
                placeholder={placeholder}
              />
            }
            placeholder={
              <div className="absolute top-4 left-4 text-gray-400 pointer-events-none">
                {placeholder}
              </div>
            }
            ErrorBoundary={ErrorBoundary}
          />
          <CustomOnChangePlugin onChange={onChange} />
          <MarkdownShortcutsPlugin />
          <SmartURLDetectionPlugin />
          <HistoryPlugin />
          <AutoFocusPlugin />
        </div>
        {/* Hidden file input for image uploads */}
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          style={{ display: 'none' }}
          onChange={handleImageChange}
        />
        {/* Image upload button in toolbar */}
        <div className="p-2">
          <button
            type="button"
            className="px-3 py-1 bg-blue-100 text-blue-700 rounded hover:bg-blue-200"
            onClick={() => fileInputRef.current?.click()}
          >
            Upload Image
          </button>
          <span className="ml-2 text-xs text-gray-500">Drag & drop or click to upload an image</span>
        </div>
      </LexicalComposer>
    </div>
  );
};

export default RichTextEditor;
