import React, { useEffect, useMemo, useCallback } from 'react';
import { LexicalComposer } from '@lexical/react/LexicalComposer';
import { RichTextPlugin } from '@lexical/react/LexicalRichTextPlugin';
import { ContentEditable } from '@lexical/react/LexicalContentEditable';
import { HistoryPlugin } from '@lexical/react/LexicalHistoryPlugin';
import { AutoFocusPlugin } from '@lexical/react/LexicalAutoFocusPlugin';
import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot, $getSelection, $isRangeSelection, $createTextNode } from 'lexical';
import type { LexicalEditor } from 'lexical';
import { Bold, Italic, Underline, List, Link, AlignLeft, AlignCenter, AlignRight, Heading1, Heading2, Heading3 } from 'lucide-react';

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  className?: string;
}

// Markdown shortcuts plugin
function MarkdownShortcutsPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(({ editorState, dirtyElements }) => {
      if (dirtyElements.size === 0) return;

      editorState.read(() => {
        const selection = $getSelection();
        if (!$isRangeSelection(selection)) return;

        const text = selection.getTextContent();
        
        // Handle markdown shortcuts
        if (text.startsWith('**') && text.endsWith('**') && text.length > 4) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(2, -2);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('*') && text.endsWith('*') && text.length > 2) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(1, -1);
              newSelection.insertText(cleanText);
              newSelection.formatText('italic');
            }
          });
        } else if (text.startsWith('~') && text.endsWith('~') && text.length > 2) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(1, -1);
              newSelection.insertText(cleanText);
              newSelection.formatText('underline');
            }
          });
        } else if (text.startsWith('# ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(2);
              newSelection.insertText(cleanText);
              // Apply header formatting (simplified - just make it bold and larger)
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('## ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(3);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        } else if (text.startsWith('### ')) {
          editor.update(() => {
            const newSelection = $getSelection();
            if ($isRangeSelection(newSelection)) {
              const cleanText = text.slice(4);
              newSelection.insertText(cleanText);
              newSelection.formatText('bold');
            }
          });
        }
      });
    });
  }, [editor]);

  return null;
}

// Toolbar component
function ToolbarPlugin(): JSX.Element {
  const [editor] = useLexicalComposerContext();

  const formatText = (format: 'bold' | 'italic' | 'underline') => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        selection.formatText(format);
      }
    });
  };

  const formatHeader = (level: 1 | 2 | 3) => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // For now, just make headers bold
        // In a full implementation, you'd create proper header nodes
        selection.formatText('bold');
      }
    });
  };

  const alignText = (alignment: 'left' | 'center' | 'right') => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // For now, just insert alignment markers
        // In a full implementation, you'd set proper CSS alignment
        const marker = alignment === 'left' ? '' : alignment === 'center' ? ' [CENTER] ' : ' [RIGHT] ';
        selection.insertText(marker);
      }
    });
  };

  const insertLink = () => {
    const url = prompt('Enter URL:');
    if (url) {
      const linkText = prompt('Enter link text (optional):', url);
      const displayText = linkText || url;
      
      editor.update(() => {
        const selection = $getSelection();
        if ($isRangeSelection(selection)) {
          // Create markdown-style link
          const markdownLink = `[${displayText}](${url})`;
          selection.insertText(markdownLink);
        }
      });
    }
  };

  const toggleList = () => {
    editor.update(() => {
      const selection = $getSelection();
      if ($isRangeSelection(selection)) {
        // Simple list implementation - just add bullet points
        const text = selection.getTextContent();
        const lines = text.split('\n');
        const formattedLines = lines.map(line => `• ${line}`);
        selection.insertText(formattedLines.join('\n'));
      }
    });
  };

  return (
    <div className="flex items-center gap-2 p-2 border-b border-gray-200 bg-gray-50">
      {/* Text Formatting */}
      <button
        type="button"
        onClick={() => formatText('bold')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Bold (Ctrl+B)"
      >
        <Bold className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => formatText('italic')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Italic (Ctrl+I)"
      >
        <Italic className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => formatText('underline')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Underline (Ctrl+U)"
      >
        <Underline className="w-4 h-4" />
      </button>
      
      <div className="w-px h-6 bg-gray-300" />
      
      {/* Headers */}
      <button
        type="button"
        onClick={() => formatHeader(1)}
        className="p-2 hover:bg-gray-200 rounded"
        title="Heading 1 (# )"
      >
        <Heading1 className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => formatHeader(2)}
        className="p-2 hover:bg-gray-200 rounded"
        title="Heading 2 (## )"
      >
        <Heading2 className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => formatHeader(3)}
        className="p-2 hover:bg-gray-200 rounded"
        title="Heading 3 (### )"
      >
        <Heading3 className="w-4 h-4" />
      </button>
      
      <div className="w-px h-6 bg-gray-300" />
      
      {/* Alignment */}
      <button
        type="button"
        onClick={() => alignText('left')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Align Left"
      >
        <AlignLeft className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => alignText('center')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Align Center"
      >
        <AlignCenter className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={() => alignText('right')}
        className="p-2 hover:bg-gray-200 rounded"
        title="Align Right"
      >
        <AlignRight className="w-4 h-4" />
      </button>
      
      <div className="w-px h-6 bg-gray-300" />
      
      {/* Lists and Links */}
      <button
        type="button"
        onClick={toggleList}
        className="p-2 hover:bg-gray-200 rounded"
        title="Bullet List"
      >
        <List className="w-4 h-4" />
      </button>
      <button
        type="button"
        onClick={insertLink}
        className="p-2 hover:bg-gray-200 rounded"
        title="Insert Link"
      >
        <Link className="w-4 h-4" />
      </button>
    </div>
  );
}

// Smart URL detection plugin
function SmartURLDetectionPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(({ editorState, dirtyElements }) => {
      if (dirtyElements.size === 0) return;

      editorState.read(() => {
        const selection = $getSelection();
        if (!$isRangeSelection(selection)) return;

        const text = selection.getTextContent();
        
        // URL regex pattern - matches http/https URLs and www. URLs
        const urlRegex = /((https?:\/\/[^\s]+)|(www\.[^\s]+\.[^\s]+))/g;
        const matches = text.match(urlRegex);
        
        if (matches && matches.length > 0) {
          // Check if we have unformatted URLs
          const hasUnformattedUrls = matches.some(url => {
            // Check if the URL is not already in markdown link format
            return !text.includes(`[${url}]`) && !text.includes(`[${url.split('/').pop()}]`);
          });

          if (hasUnformattedUrls) {
            editor.update(() => {
              const newSelection = $getSelection();
              if ($isRangeSelection(newSelection)) {
                // Get the current text again
                const currentText = newSelection.getTextContent();
                
                // Replace URLs with markdown-style links
                let formattedText = currentText;
                matches.forEach(url => {
                  // Only format if it's not already formatted
                  if (!formattedText.includes(`[${url}]`)) {
                    // Prepend https:// if it's a www. URL
                    const fullUrl = url.startsWith('www.') ? `https://${url}` : url;
                    // Create a shorter display text for the link
                    const displayText = url.length > 50 ? url.substring(0, 47) + '...' : url;
                    formattedText = formattedText.replace(url, `[${displayText}](${fullUrl})`);
                  }
                });
                
                if (formattedText !== currentText) {
                  // Replace the entire selection with formatted text
                  newSelection.insertText(formattedText);
                }
              }
            });
          }
        }
      });
    });
  }, [editor]);

  return null;
}

// Error boundary component
function ErrorBoundary({ children }: { children: React.ReactNode }): JSX.Element {
  return <div>{children}</div>;
}

const RichTextEditor: React.FC<RichTextEditorProps> = ({ 
  value, 
  onChange, 
  placeholder = "Start writing...",
  className = ""
}) => {
  // Set up initial editor state from value (serialized state or plain text)
  const initialEditorState = useCallback((editor: LexicalEditor) => {
    if (typeof value === 'string' && value.trim().length > 0) {
      let parsed: any = null;
      try {
        parsed = JSON.parse(value);
      } catch {
        // not JSON, treat as plain text
      }
      if (parsed && typeof parsed === 'object' && parsed.root) {
        // Looks like a Lexical serialized state
        editor.setEditorState(editor.parseEditorState(value));
      } else {
        // Fallback: treat as plain text
        editor.update(() => {
          const root = $getRoot();
          root.clear();
          root.append($createTextNode(value));
        });
      }
    }
  }, [value]);

  // Custom onChange plugin for serialized state
  function SerializedOnChangePlugin({ onChange }: { onChange: (value: string) => void }): JSX.Element | null {
    const [editor] = useLexicalComposerContext();
    useEffect(() => {
      return editor.registerUpdateListener(({ editorState }) => {
        // Serialize the editor state as JSON string
        const serialized = JSON.stringify(editorState.toJSON());
        onChange(serialized);
      });
    }, [editor, onChange]);
    return null;
  }

  const initialConfig = {
    namespace: 'BeedooRichTextEditor',
    theme: {
      root: 'outline-none min-h-[200px] p-4',
      text: {
        bold: 'font-bold',
        italic: 'italic',
        underline: 'underline',
      },
    },
    onError: (error: Error) => {
      console.error('Lexical error:', error);
    },
    editorState: null,
    editable: true,
    initialEditorState,
  };

  return (
    <div className={`border border-gray-300 rounded-md ${className}`}>
      <LexicalComposer initialConfig={initialConfig}>
        <ToolbarPlugin />
        <div className="relative">
          <RichTextPlugin
            contentEditable={
              <ContentEditable 
                className="outline-none min-h-[200px] p-4"
              />
            }
            placeholder={
              <div className="absolute top-4 left-4 text-gray-400 pointer-events-none">
                {placeholder}
              </div>
            }
            ErrorBoundary={ErrorBoundary}
          />
          <SerializedOnChangePlugin onChange={onChange} />
          <MarkdownShortcutsPlugin />
          <SmartURLDetectionPlugin />
          <HistoryPlugin />
          <AutoFocusPlugin />
        </div>
      </LexicalComposer>
    </div>
  );
};

export default RichTextEditor;
