import React, { useState, useEffect } from 'react';
import { Task, TaskStatistics } from './types/Task';
import { loadTasksFromLocalStorage, saveTasksToLocalStorage, calculateTaskStats } from './utils/taskUtils';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import TaskStats from './components/TaskStats';
import { Plus, Menu, X } from 'lucide-react';

function App() {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [showForm, setShowForm] = useState(false);
  const [showStats, setShowStats] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [showCompletedTasks, setShowCompletedTasks] = useState(true);

  useEffect(() => {
    const savedTasks = loadTasksFromLocalStorage();
    setTasks(savedTasks);
  }, []);

  useEffect(() => {
    saveTasksToLocalStorage(tasks);
  }, [tasks]);

  const handleAddTask = (newTask: Task) => {
    setTasks(prev => [...prev, newTask]);
    setShowForm(false);
  };

  const handleUpdateTask = (id: string, updates: Partial<Task>) => {
    setTasks(prev => {
      const updated = prev.map(task => {
        if (task.id === id) {
          return { ...task, ...updates, updatedAt: new Date() };
        }
        return task;
      });
      return updated;
    });
  };

  const handleDeleteTask = (id: string) => {
    setTasks(prev => prev.filter(task => task.id !== id));
  };

  const handleAddSubtask = (parentId: string, subtask: Task) => {
    setTasks(prev => {
      return prev.map(task => {
        if (task.id === parentId) {
          return {
            ...task,
            subtasks: [...task.subtasks, subtask],
            updatedAt: new Date(),
          };
        }
        return task;
      });
    });
  };

  const handleAddNextStep = (parentId: string, nextStep: Task) => {
    setTasks(prev => {
      return prev.map(task => {
        if (task.id === parentId) {
          return {
            ...task,
            nextSteps: [...task.nextSteps, nextStep],
            updatedAt: new Date(),
          };
        }
        return task;
      });
    });
  };

  const stats = calculateTaskStats(tasks);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-semibold text-gray-900">Beedoo Task Manager</h1>
            </div>
            
            {/* Desktop Actions */}
            <div className="hidden md:flex items-center space-x-4">
              <button
                onClick={() => setShowCompletedTasks(!showCompletedTasks)}
                className={`px-4 py-2 text-sm font-medium rounded-md border focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 ${
                  showCompletedTasks 
                    ? 'text-gray-700 bg-white border-gray-300 hover:bg-gray-50' 
                    : 'text-white bg-gray-600 border-gray-600 hover:bg-gray-700'
                }`}
              >
                {showCompletedTasks ? 'Hide Completed' : 'Show Completed'}
              </button>
              <button
                onClick={() => setShowStats(!showStats)}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              >
                Stats
              </button>
              <button
                onClick={() => setShowForm(true)}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Task
              </button>
            </div>

            {/* Mobile menu button */}
            <div className="md:hidden">
              <button
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-primary-500"
              >
                {isMobileMenuOpen ? (
                  <X className="block h-6 w-6" />
                ) : (
                  <Menu className="block h-6 w-6" />
                )}
              </button>
            </div>
          </div>

          {/* Mobile menu */}
          {isMobileMenuOpen && (
            <div className="md:hidden py-4 border-t border-gray-200">
              <div className="flex flex-col space-y-2">
                <button
                  onClick={() => {
                    setShowCompletedTasks(!showCompletedTasks);
                    setIsMobileMenuOpen(false);
                  }}
                  className={`px-4 py-2 text-sm font-medium rounded-md border ${
                    showCompletedTasks 
                      ? 'text-gray-700 bg-white border-gray-300 hover:bg-gray-50' 
                      : 'text-white bg-gray-600 border-gray-600 hover:bg-gray-700'
                  }`}
                >
                  {showCompletedTasks ? 'Hide Completed' : 'Show Completed'}
                </button>
                <button
                  onClick={() => {
                    setShowStats(!showStats);
                    setIsMobileMenuOpen(false);
                  }}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
                >
                  Stats
                </button>
                <button
                  onClick={() => {
                    setShowForm(true);
                    setIsMobileMenuOpen(false);
                  }}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add Task
                </button>
              </div>
            </div>
          )}
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Task List */}
          <div className="lg:col-span-3">
            <TaskList
              tasks={tasks}
              onUpdateTask={handleUpdateTask}
              onDeleteTask={handleDeleteTask}
              onAddSubtask={handleAddSubtask}
              onAddNextStep={handleAddNextStep}
              showCompletedTasks={showCompletedTasks}
            />
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            {showStats && (
              <div className="bg-white rounded-lg shadow p-6 mb-6">
                <TaskStats stats={stats} />
              </div>
            )}
            
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Quick Actions</h3>
              <div className="space-y-3">
                <button
                  onClick={() => setShowCompletedTasks(!showCompletedTasks)}
                  className={`w-full inline-flex items-center justify-center px-4 py-2 border text-sm font-medium rounded-md focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500 ${
                    showCompletedTasks 
                      ? 'text-gray-700 bg-white border-gray-300 hover:bg-gray-50' 
                      : 'text-white bg-gray-600 border-gray-600 hover:bg-gray-700'
                  }`}
                >
                  {showCompletedTasks ? 'Hide Completed Tasks' : 'Show Completed Tasks'}
                </button>
                <button
                  onClick={() => setShowForm(true)}
                  className="w-full inline-flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  New Task
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>

      {/* Task Form Modal */}
      {showForm && (
        <TaskForm
          onSubmit={handleAddTask}
          onCancel={() => setShowForm(false)}
        />
      )}
    </div>
  );
}

export default App;
