import React from 'react';
import { Task } from '../types/Task';
import TaskItem from './TaskItem';

interface TaskListProps {
  tasks: Task[];
  onUpdateTask: (id: string, updates: Partial<Task>) => void;
  onDeleteTask: (id: string) => void;
  onAddSubtask: (parentId: string, subtask: Task) => void;
  onAddNextStep: (parentId: string, nextStep: Task) => void;
  showCompletedTasks: boolean;
}

const TaskList: React.FC<TaskListProps> = ({
  tasks,
  onUpdateTask,
  onDeleteTask,
  onAddSubtask,
  onAddNextStep,
  showCompletedTasks,
}) => {
  if (tasks.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow p-8 text-center">
        <div className="text-gray-500">
          <svg
            className="mx-auto h-12 w-12 text-gray-400"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            aria-hidden="true"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"
            />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No tasks</h3>
          <p className="mt-1 text-sm text-gray-500">
            Get started by creating a new task.
          </p>
        </div>
      </div>
    );
  }

  // Filter tasks based on global completed tasks visibility
  const filteredTasks = showCompletedTasks 
    ? tasks 
    : tasks.filter(task => !task.completed);

  // Group tasks by priority for visual organization
  const priorityGroups = {
    urgent: filteredTasks.filter(task => task.priority === 'urgent'),
    high: filteredTasks.filter(task => task.priority === 'high'),
    medium: filteredTasks.filter(task => task.priority === 'medium'),
    low: filteredTasks.filter(task => task.priority === 'low')
  };

  const getPriorityHeader = (priority: string) => {
    const colors = {
      urgent: 'bg-red-50 border-red-200 text-red-800',
      high: 'bg-orange-50 border-orange-200 text-orange-800',
      medium: 'bg-yellow-50 border-yellow-200 text-yellow-800',
      low: 'bg-green-50 border-green-200 text-green-800'
    };
    
    return (
      <div className={`px-4 py-2 border-l-4 ${colors[priority as keyof typeof colors]} bg-opacity-50`}>
        <h3 className="text-sm font-semibold uppercase tracking-wide">
          {priority.charAt(0).toUpperCase() + priority.slice(1)} Priority ({priorityGroups[priority as keyof typeof priorityGroups].length})
        </h3>
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {Object.entries(priorityGroups).map(([priority, tasks]) => 
        tasks.length > 0 && (
          <div key={priority} className="space-y-2">
            {getPriorityHeader(priority)}
            <div className="space-y-4">
              {tasks.map((task) => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onUpdateTask={onUpdateTask}
                  onDeleteTask={onDeleteTask}
                  onAddSubtask={onAddSubtask}
                  onAddNextStep={onAddNextStep}
                  level={0}
                  showCompletedTasks={showCompletedTasks}
                />
              ))}
            </div>
          </div>
        )
      )}
    </div>
  );
};

export default TaskList;
